import BubbleChat from "./BubbleChat";

function Contact({ picture, name, lastMessage, lastSeen, setCurrentChat, setCurrentPicture, chatList,setCurrentChatList }) {
  const handleClick = (event) => {
    setCurrentChat(name);
    setCurrentPicture(picture);
    setCurrentChatList(chatList);
    const addContact = (name) => {
      const newContact = {
        picture: "logo.jpg",
        name: name,
        lastMessage: "",
        lastSeen: "",
        chatList: [] // Create an empty chat list for the new contact
      };
      setContacts((prevContacts) => [...prevContacts, newContact]);
    };
  };

  return (
    <div className="contact_profile" id="contact_profile_main" onClick={handleClick}>
      <img src={picture} alt="profile picture" className="chat_profile_picture" />
      <div className="name_last_seen">
        <b className="textColor contacts_chat">{name}</b>
        <p className="last_message">{lastMessage}</p>
      </div>
      <div className="last_seen">{lastSeen}</div>
      <div className="chat_list">
        {chatList.map((message, index) => (
          <BubbleChat
            key={index}
            text={message.text}
            sender={message.sender}
            time={message.time}
          />
        ))}
      </div>
    </div>
  );
}

export default Contact;
