import React, { useState } from 'react';

import { BrowserRouter, Routes, Route } from 'react-router-dom';
import './colors.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import LoginPage from './login/LoginPage';
import RegisterPage from './register/RegisterPage';
import ChatPage from './chat/ChatPage'




function App() {
  var adminContacts = [];
        var chats = [];
        var newUser = {
          username:"admin",
           password:"1234",
           displayName:"Admin",
           previewImg:"/logo.jpg",
           contacts:adminContacts,
          chats:chats}
  const [usernameList, setUsernameList] = useState(["admin"]);
  const [userList, setUserList] = useState([newUser]);
  const [loginStatus, setLoginStatus] = useState(false);
  const [currentUser, setCurrentUser] = useState([]);
  const [userImage, setUserImage] = useState("/logo.jpg");
  const [displayName, setDisplayName] = useState("");
  const [contacts, setContacts] = useState([]);
  const [currentChat, setCurrentChat] = useState([]);
  const [chatList, setCurrentChatList] = useState([]);



  return (
    <BrowserRouter>
      <Routes>
        <Route path="/" element={<LoginPage setLoginStatus={setLoginStatus} setCurrentUser={setCurrentUser} usernameList={usernameList} userList={userList} setContacts={setContacts} setDisplayName={setDisplayName} setUserImage={setUserImage} />} />
        <Route path="/register" element={<RegisterPage displayName={displayName} setDisplayName={setDisplayName} setUserImage={setUserImage} usernameList={usernameList} setUsernameList={setUsernameList} userList={userList} setUserList={setUserList} />} />
        <Route path="/chat" element={<ChatPage chatList={chatList} setCurrentChatList={setCurrentChatList} currentChat={currentChat} setCurrentChat={setCurrentChat} userImage={userImage} contacts={contacts} setContacts={setContacts} displayName={displayName} loginStatus={loginStatus} setLoginStatus={setLoginStatus} currentUser={currentUser} />} />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
