import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import Contact from "./Contact";
import BubbleChat from "./BubbleChat";
import MyProfile from "./MyProfile";
import SendBox from "./SendBox";
import $ from 'jquery';

function ChatPage({ chatList, setCurrentChatList, loginStatus, setLoginStatus, currentUser, userImage, displayName,contacts,setContacts, currentChat, setCurrentChat }) {
 

  const [currentPicture, setCurrentPicture] = useState("logo.jpg");
  const navigate = useNavigate();
   
  
  useEffect(() => {
    if(loginStatus === false){
      navigate("/");
  }
    // Add any initial contacts here if needed
  }, []);
 

  const logOut = (event) => {
    setCurrentChat("");
    setCurrentPicture("");
    setCurrentChatList([]);
    setLoginStatus(false);
    navigate("/");
  };

  const addContact = (name) => {
    for(var i=0; i<currentUser.contacts.length; i++){
      if(currentUser.contacts[i].name === name) {
        alert("Contact already exist!");
        $("#contactName").val("");
        return;
      }
    }
    const newContact = {
      picture: "logo.jpg",
      name: name,
      lastMessage: "no message yet",
      lastSeen: "now",
      chatList: [] // Create an empty chat list for the new contact
    };
    setContacts((prevContacts) => [...prevContacts, newContact]);
    if((currentUser.contacts).length == 0) {
        currentUser.contacts = [newContact];
    } else {
        currentUser.contacts.push(newContact);
    }
    $("#contactName").val("");

    

  };
  
 

  return (
    <>
      <button type="button" className="btn btn-outline-light btn-sm textColor" id="logout" onClick={logOut}>
        Logout
      </button>

      <main id="main_chat">
        <div id="contacts" className="column">
          <MyProfile picture={userImage} name={displayName} />
          <div id="contacts-list">
          {contacts.map((contact, index) => (
  <Contact
    key={index}
    picture={contact.picture}
    name={contact.name}
    lastMessage={contact.lastMessage}
    lastSeen={contact.lastSeen}
    setCurrentChat={setCurrentChat}
    setCurrentPicture={setCurrentPicture}
    setCurrentChatList={setCurrentChatList}
    chatList={contact.chatList} // Pass the chatList prop
    currentUser={currentUser}
  />
))}
          </div>
          


          <button className="btn btn-primary" id="add_contact_button" data-toggle="modal" data-target="#addContactModal">
            Add Contact
          </button>

          <div className="modal fade" id="addContactModal" tabIndex="-1" role="dialog" aria-labelledby="addContactModalLabel" aria-hidden="true">
            <div className="modal-dialog" role="document">
              <div className="modal-content">
                <div className="modal-header">
                  <h5 className="modal-title" id="addContactModalLabel">
                    Add Contact
                  </h5>
                  <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                    <span aria-hidden="true">&times;</span>
                  </button>
                </div>
                <div className="modal-body">
                  <form>
                    <div className="form-group">
                      <label htmlFor="contactName">Name</label>
                      <input type="text" className="form-control" id="contactName" placeholder="Enter contact nickname" />
                    </div>
                  </form>
                </div>
                <div className="modal-footer">

                <button type="button" className="btn btn-secondary" data-dismiss="modal">
                    Close
                  </button>
                  <button
                    type="button"
                    className="btn btn-primary"
                    onClick={() => addContact(document.getElementById("contactName").value)}
                    data-dismiss="modal"
                  >
                    Add
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
        <div id="chat">
          <div id="chat_header">
            <img src={currentPicture} alt="profile picture" id="chat_profile_picture_2" />
            <b className="textColor contatcs_chat">{currentChat}</b>
          </div>
          <div id="chat_box">
            {chatList.map((chat) => (
              <BubbleChat  text={chat.text} time={chat.time} sender={chat.sender} />
            ))}
          </div>
                <SendBox contacts={contacts} setCurrentChatList={setCurrentChatList} chatList={chatList} currentUser={currentUser} currentChat={currentChat} />
          
        </div>
      </main>

    </>
  );
}

export default ChatPage;
