import { event } from "jquery";

function BubbleChat({ text, sender, time }) {
    

    if (sender === "send") {
        return (
            <>
                <div className="bubble right">{text}
                    <div className="last_seen">{time}</div>
                </div>
            </>

        )
    } else if(sender === "recieve") {
        return (
            <>
                <div className="bubble left">{text}
                    <div className="last_seen">{time}</div>
                </div>
            </>
        )
    }

}
export default BubbleChat;
