import BubbleChat from "./BubbleChat";

function Contact({ currentUser, picture, name, lastMessage, lastSeen, setCurrentChat, setCurrentPicture, chatList,setCurrentChatList }) {
  const handleClick = (event) => {
    setCurrentChat(name);
    setCurrentPicture(picture);
    setCurrentChatList(chatList);
  };

  var lastMessageTime;

  if(chatList.length === 0) {
    lastMessage = "no messages yet";
    lastMessageTime = "";
    
  } else {
    lastMessage = chatList[chatList.length - 1].text ;
    lastMessageTime = chatList[chatList.length - 1].time;
  }



  return (
    <div className="contact_profile" id="contact_profile_main" onClick={handleClick}>
      <img src={picture} alt="profile picture" className="chat_profile_picture" />
      <div className="name_last_seen">
        <b className="textColor contacts_chat">{name}</b>
        <p className="last_message"> {lastMessage} <br/> {lastMessageTime} </p>

      </div>
      <div className="last_seen">{lastSeen}</div>
      
    </div>
  );
}

export default Contact;
