import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import TextInput from '../register/TextInput';
import PasswordInput from '../register/PasswordInput';


function LoginPage({ setLoginStatus, setCurrentUser, userList, usernameList, setContacts, setDisplayName, setUserImage }) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const navigate = useNavigate();

  const login =async (event) => {
    var UserLogin = {
      "username":username,
       "password":password
      };
      const res = await fetch('http://localhost:5000/api/Tokens', {
        'method': 'post', // send a post request
        'headers': {
        'Content-Type': 'application/json', // the data (username/password) is in the form of a JSON object
        },
        'body': JSON.stringify(UserLogin) // The actual data (username/password)
        }
        )
        // The server's response is a json object
       
       alert(res.body);

  }

  return (
    <main>
      <div className="login">
        <form className="login">
          <TextInput className="username-login" userList={userList} usernameList={usernameList} username={username} setUsername={setUsername} />
          <div className="form-group">
            <PasswordInput className="password-login" password={password} setPassword={setPassword} />
          </div>
          <button type="button" className="btn btn-outline-light btn-sm textColor" onClick={login}>Login</button> <br />
          <small className="textColor">Don't have an account yet? <Link className="textColor" to="/register">Register</Link></small>
        </form>

      </div>
    </main>


  );
}


export default LoginPage;
