function Contact({picture, name, lastMessage, lastSeen, setCurrentChat, setCurrentPicture}) {
    const handleClick = (event) => {
        setCurrentChat(name);
        setCurrentPicture(picture);
    }

    return (
        <div class="contact_profile" id="contact_profile_main" onClick={handleClick}>
            <img src={picture}
            alt="profile picture" 
            class="chat_profile_picture" />
            <div class="name_last_seen">
                <b class="textColor contatcs_chat">{name}</b>
                <p class="last_massege">{lastMessage}</p>
            </div>
            <div class="last_seen">{lastSeen}</div>
        </div>

    )
}
export default Contact;
