function Contact({ picture, name, setCurrentChat, setCurrentPicture,  token, id, setMessages, setChatId, chatId, lastMessage }) {
  const handleClick = async (event) => {
     setCurrentChat(name);
     setCurrentPicture(picture);
    await setChatId(id);
    console.log(chatId)
    getMessages();
  };

  const getMessages = async (event) => {
    const res = await fetch(`http://localhost:5000/api/Chats/${id}/Messages`, {
      method: 'get',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`
      },
    });

    if (res.ok) {
      const  messages  = await res.json();
      setMessages(messages);
    } else {
      alert("Unable to fetch messages");
    }
  };


  return (
    <div className="contact_profile" id="contact_profile_main" onClick={handleClick}>
      <img src={picture} alt="profile picture" className="chat_profile_picture" />
      <div className="name_last_seen">
        <b className="textColor contacts_chat">{name}</b>
        {lastMessage && (
          <>
            <p className="last_message">{lastMessage.content}</p>
            <div className="last_seen">{lastMessage.created}</div>
          </>
        )}
      </div>
    </div>
  );
}

export default Contact;
