import React, { useState } from 'react';

import { BrowserRouter, Routes, Route } from 'react-router-dom';
import './colors.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import LoginPage from './login/LoginPage';
import RegisterPage from './register/RegisterPage';
import ChatPage from './chat/ChatPage'




function App() {
  const [usernameList, setUsernameList] = useState([]);
  const [userList, setUserList] = useState([]);
  const [loginStatus, setLoginStatus] = useState(false);
  const [currentUser, setCurrentUser] = useState("My Username");
  const [userImage, setUserImage] = useState("/logo.jpg");
  const [displayName, setDisplayName] = useState("");
  const [contacts, setContacts] = useState([]);


  return (
    <BrowserRouter>
      <Routes>
        <Route path="/" element={<LoginPage setLoginStatus={setLoginStatus} setCurrentUser={setCurrentUser} usernameList={usernameList} userList={userList}  />} />
        <Route path="/register" element={<RegisterPage displayName={displayName} contacts={contacts} setContacts={setContacts} setDisplayName={setDisplayName} setUserImage={setUserImage} usernameList={usernameList} setUsernameList={setUsernameList} userList={userList} setUserList={setUserList} />} />
        <Route path="/chat" element={<ChatPage userImage={userImage} contacts={contacts} changeContacts={changeContacts} displayName={displayName} loginStatus={loginStatus} setLoginStatus={setLoginStatus} currentUser={currentUser} />} />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
