function BubbleChat({ username, sender, content, created }) {
    

    if (sender.username === username) {
        return (
            <>
                <div className="bubble right">{content}
                    <div className="last_seen">{created}</div>
                </div>
            </>

        )
    } else  {
        return (
            <>
                <div className="bubble left">{content}
                    <div className="last_seen">{created}</div>
                </div>
            </>
        )
    }

}
export default BubbleChat;
