import React from 'react';
import { Link } from 'react-router-dom';


class LoginPage extends React.Component {
  render() {
    return (
      <main>
        <div className="login">
          <form className="login">
            <div className="form-group">
              <label htmlFor="username" className="textColor">Username:</label>
              <input type="text" className="form-control" id="username" name="username" aria-describedby="emailHelp" placeholder="Enter username" required pattern="[a-zA-Z0-9]{4,20}" title="Username must be 4-20 characters and can only contain letters and numbers." />
            </div>
            <div className="form-group">
              <label htmlFor="password" className="textColor">Password:</label>
              <input type="password" className="form-control" id="password" name="password" placeholder="Password" required pattern="^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[^\da-zA-Z]).{8,}$" title="Password must be at least 8 characters long, contain at least one uppercase letter, one lowercase letter, one digit, and one special character." />
              <small id="exampleInputPassword1" className=" textColor">We'll never share your Password with anyone else.</small>
            </div>
            <button type="submit" className="btn btn-outline-light btn-sm textColor">Login</button> <br />
            <small class="textColor">Don't have an account yet? <Link to="/register">Register</Link></small>
          </form>
         
        </div>
      </main>


    );
  }
}

export default LoginPage;
