import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import Contact from "./Contact";
import BubbleChat from "./BubbleChat";
import MyProfile from "./MyProfile";
import SendBox from "./SendBox";

function ChatPage({  loginStatus, setLoginStatus, currentUser, contacts, setContacts, currentChat, setCurrentChat, token, messages, setMessages, chatId, setChatId }) {


  const [currentPicture, setCurrentPicture] = useState("logo.jpg");
  const navigate = useNavigate();


  useEffect(() => {
    if (loginStatus === false) {
      navigate("/");
    }
    console.log(contacts);
    getChats();
    // Add any initial contacts here if needed
  }, []);

  const getMessages = async (event) => {
    const res = await fetch(`http://localhost:5000/api/Chats/${chatId}/Messages`, {
      method: 'get',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`
      },
    });

    if (res.ok) {
      const  messages  = await res.json();
      setMessages(messages);
    } else {
      alert("Unable to fetch messages");
    }
  };

  const getChats = async (event) => {
    const newChatList = await fetch(`http://localhost:5000/api/Chats`, {
      method: 'get',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`
      }
    })
    console.log(newChatList);
    const chatJson = await newChatList.json();
    console.log(chatJson);
    setContacts(chatJson);
  }


  const logOut = (event) => {
    setCurrentChat("");
    setCurrentPicture("");
    setContacts([]);
    setLoginStatus(false);
    setMessages([]);
    navigate("/");
  };

  const addContact = async (name) => {
    const newContact = {
      "username": name
    };
    const res = await fetch('http://localhost:5000/api/Chats', {
      'method': 'post', // send a post request
      'headers': {
        'Content-Type': 'application/json', // the data (username/password) is in the form of a JSON object
        'Authorization': `Bearer ${token}`
      },
      'body': JSON.stringify(newContact) // The actual data (username/password)
    }
    )
    if (res.ok) {
      console.log("added user");
      await getChats();
    } else {
      // Handle error cases
      alert("no such username");
    }



  };

  const handleSendMessage = async (message) => {
    const newMessage = {
      "msg": message
    };
    const res = await fetch(`http://localhost:5000/api/Chats/${chatId}/Messages`, {
      method: 'post',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`
      },
      body: JSON.stringify(newMessage)
    });

    if (res.ok) {
      console.log("added message");
      getMessages();
      getChats();
      testGetChats();
    } else {
      alert("Unable to send message");
    }
  };

  const testGetChats = async event => {
    const res = await fetch(`http://localhost:5000/api/Chats/${chatId}`, {
      method: 'get',
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${token}`
      }
    })
    if(res.ok){
      const data = await res.json();
      console.log(data)
    }
    else{
      alert('Unable to fetch chats')
    } 
  }



  return (
    <>
      <button type="button" className="btn btn-outline-light btn-sm textColor" id="logout" onClick={logOut}>
        Logout
      </button>

      <main id="main_chat">
        <div id="contacts" className="column">
          <MyProfile picture={currentUser.profilePic} name={currentUser.displayName} />
          <div id="contacts-list">
            {contacts.map((contact, index) => (
              <Contact
                key={contact.id}
                id={contact.id}
                picture={contact.user.profilePic}
                name={contact.user.displayName}
                lastMessage={contact.lastMessage}
                setCurrentChat={setCurrentChat}
                setCurrentPicture={setCurrentPicture}
                token={token}
                currentUser={currentUser}
                setMessages={setMessages}
                setChatId={setChatId}
                chatId={chatId}
                setContacts={setContacts}
              />
            ))}
          </div>



          <button className="btn btn-primary" id="add_contact_button" data-toggle="modal" data-target="#addContactModal">
            Add Contact
          </button>

          <div className="modal fade" id="addContactModal" tabIndex="-1" role="dialog" aria-labelledby="addContactModalLabel" aria-hidden="true">
            <div className="modal-dialog" role="document">
              <div className="modal-content">
                <div className="modal-header">
                  <h5 className="modal-title" id="addContactModalLabel">
                    Add Contact
                  </h5>
                  <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                    <span aria-hidden="true">&times;</span>
                  </button>
                </div>
                <div className="modal-body">
                  <form>
                    <div className="form-group">
                      <label htmlFor="contactName">Name</label>
                      <input type="text" className="form-control" id="contactName" placeholder="Enter contact nickname" />
                    </div>
                  </form>
                </div>
                <div className="modal-footer">

                  <button type="button" className="btn btn-secondary" data-dismiss="modal">
                    Close
                  </button>
                  <button
                    type="button"
                    className="btn btn-primary"
                    onClick={() => addContact(document.getElementById("contactName").value)}
                    data-dismiss="modal"
                  >
                    Add
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
        <div id="chat">
          <div id="chat_header">
            <img src={currentPicture} alt="profile picture" id="chat_profile_picture_2" />
            <b className="textColor contatcs_chat">{currentChat}</b>
          </div>
          <div id="chat_box">
            {messages && (messages.reverse().map((message, index) => (
              <BubbleChat username={currentUser.username} sender={message.sender} content={message.content} created={message.created} />
            )))}
          </div>
          <SendBox sendMessage={handleSendMessage} />
        </div>
      </main>

    </>
  );
}

export default ChatPage;
