import { useEffect, useState } from "react";
import $ from 'jquery';
import { render } from "react-dom";

function SendBox({chatList, setCurrentChatList, currentUser, currentChat, contacts, setCurrenChat}) {
    const [message, setMessage] = useState("");
    const handleTextChange = (event) => {
        setMessage(event.target.value);
    }

  

    const sendMessage = (event) => {
        if(message === "") {
            return;
        }
        var newChat = chatList;
        var newMessage = message.slice();
        newChat.push({text:newMessage, sender:"send", time: new Date().toLocaleTimeString()});
        setCurrentChatList((prevChat) => [...prevChat, newMessage]);
        const contact = contacts.find(contact => contact.name === currentChat);
        let index = contacts.indexOf(contact);
        contacts.splice(index,1);
        contacts.unshift(contact);
        contact.chatList = chatList;
        $("#send").val("");
        setMessage("");
    }


    return (
        <div id="send_message">
            <input type="text"  onChange={handleTextChange} className="form-control rounded-0" id="send" aria-describedby="emailHelp" placeholder="Enter your message here..." />
            <button type="button" className="btn btn-outline-light btn-sm textColor" id="send_btn" onClick={sendMessage}>
                Send
            </button>
            <button type="button" className="btn btn-outline-light btn-sm textColor" id="send_btn">
                Attach A File
            </button>
        </div>
    )
}
export default SendBox;
