import { useEffect, useState } from "react";
import { useNavigate } from 'react-router-dom';

import Contact from "./Contact";
import contactData from "./contacts.json";
import chats from "./chats.json"
import BubbleChat from "./BubbleChat";
import MyProfile from "./MyProfile";

function ChatPage({loginStatus, setLoginStatus, currentUser, userImage}) {
    const [contacts, setContacts] = useState([]);
    const [currentChat, setCurrentChat] = useState("Nitay Shafrir");
    const [currentPicture, setCurrentPicture] = useState("nitay.jpeg");
    const navigate = useNavigate();

    useEffect(() => {
        setContacts(contactData);
    })

    const logOut = (event) => {
        setLoginStatus(false);
        navigate('/');
    }
    if(!loginStatus){
        navigate('/');
    }

   
    return (
        <>
            <button type="button" className="btn btn-outline-light btn-sm textColor" id="logout" onClick={logOut}>Logout</button>

            <main id="main_chat">

                <div id="contacts" className="column">
                    <MyProfile picture="logo.jpg" name={currentUser} />

                    {contacts.map((contact) => (
                        <Contact picture={contact.picture} name={contact.name} lastMessage={contact.lastMessage} lastSeen={contact.lastSeen} setCurrentChat={setCurrentChat} setCurrentPicture={setCurrentPicture} />
                    ))}

                    <button className="btn btn-primary" id="add_contact_button" data-toggle="modal" data-target="#addContactModal">
                        Add Contact
                    </button>
                    <div className="modal fade" id="addContactModal" tabindex="-1" role="dialog" aria-labelledby="addContactModalLabel" aria-hidden="true">
                        <div class="modal-dialog" role="document">
                            <div class="modal-content">
                                <div class="modal-header">
                                    <h5 class="modal-title" id="addContactModalLabel">Add Contact</h5>
                                    <button type="button" class="close" data-dismiss="modal" aria-label="Close">
                                        <span aria-hidden="true">&times;</span>
                                    </button>
                                </div>
                                <div class="modal-body">
                                    <form>
                                        <div class="form-group">
                                            <label for="contactName">Name</label>
                                            <input type="text" class="form-control" id="contactName" placeholder="Enter contact nickname" />
                                        </div>
                                    </form>
                                </div>
                                <div class="modal-footer">
                                    <button type="button" class="btn btn-secondary" data-dismiss="modal">Close</button>
                                    <button type="button" class="btn btn-primary" data-dismiss="modal">Add</button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                <div id="chat">
                    <div id="chat_header">
                        <img src={currentPicture} alt="profile picture" id="chat_profile_picture_2" />
                        <b className="textColor contatcs_chat">{currentChat}</b>
                    </div>


                    <div id="chat_box">
                        {chats.map((chat => (
                            <BubbleChat text={chat.text} time={chat.time} sender={chat.sender}  />
                        )))}
                    </div>

                    <div id="send_message">
                        <input type="text" className="form-control rounded-0" id="send" aria-describedby="emailHelp" placeholder="Enter your message here..." />
                        <button type="button" className="btn btn-outline-light btn-sm textColor" id="send_btn">Send</button>
                        <button type="button" className="btn btn-outline-light btn-sm textColor" id="send_btn">Attach A File</button>

                    </div>

                </div>
            </main>
            <script src="https://code.jquery.com/jquery-3.3.1.slim.min.js" integrity="sha384-q8i/X+965DzO0rT7abK41JStQIAqVgRVzpbzo5smXKp4YfRvH+8abtTE1Pi6jizo" crossorigin="anonymous"></script>
            <script src="https://cdnjs.cloudflare.com/ajax/libs/popper.js/1.14.7/umd/popper.min.js" integrity="sha384-UO2eT0CpHqdSJQ6hJty5KVphtPhzWj9WO1clHTMGa3JDZwrnQq4sF86dIHNDz0W1" crossorigin="anonymous"></script>
            <script src="https://stackpath.bootstrapcdn.com/bootstrap/4.3.1/js/bootstrap.min.js" integrity="sha384-JjSmVgyd0p3pXB1rRibZUAYoIIy6OrQ6VrjIEaFf/nJGzIxFDsf4x0xIM+B07jRM" crossorigin="anonymous"></script>
        </>
    )
}

export default ChatPage;
