import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import Contact from "./Contact";
import BubbleChat from "./BubbleChat";
import MyProfile from "./MyProfile";

function ChatPage({ loginStatus, setLoginStatus, currentUser, userImage, displayName }) {
  const [contacts, setContacts] = useState([]);
  const [currentChat, setCurrentChat] = useState("");
  const [currentPicture, setCurrentPicture] = useState("logo.jpg");
  const [chats, setChats] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    // Add any initial contacts here if needed
  }, []);

  const logOut = (event) => {
    setLoginStatus(false);
    navigate("/");
  };

  const addContact = (name) => {
    const newContact = {
      picture: "logo.jpg",
      name: name,
      lastMessage: "",
      lastSeen: "",
      chatList: []
    };
    setContacts((prevContacts) => [...prevContacts, newContact]);
  };

  useEffect(() => {
    // Retrieve chat messages for the selected contact
    const selectedContact = contacts.find((contact) => contact.name === currentChat);
    if (selectedContact) {
      setChats(selectedContact.chatList);
    } else {
      setChats([]);
    }
  }, [currentChat, contacts]);

  return (
    <>
      <button type="button" className="btn btn-outline-light btn-sm textColor" id="logout" onClick={logOut}>
        Logout
      </button>

      <main id="main_chat">
        <div id="contacts" className="column">
          <MyProfile picture={userImage} name={displayName} />

          {contacts.map((contact, index) => (
            <Contact
              key={index}
              picture={contact.picture}
              name={contact.name}
              lastMessage={contact.lastMessage}
              lastSeen={contact.lastSeen}
              setCurrentChat={setCurrentChat}
              setCurrentPicture={setCurrentPicture}
            />
          ))}

          <button className="btn btn-primary" id="add_contact_button" data-toggle="modal" data-target="#addContactModal">
            Add Contact
          </button>

          <div className="modal fade" id="addContactModal" tabIndex="-1" role="dialog" aria-labelledby="addContactModalLabel" aria-hidden="true">
            <div className="modal-dialog" role="document">
              <div className="modal-content">
                <div className="modal-header">
                  <h5 className="modal-title" id="addContactModalLabel">
                    Add Contact
                  </h5>
                  <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                    <span aria-hidden="true">&times;</span>
                  </button>
                </div>
                <div className="modal-body">
                  <form>
                    <div className="form-group">
                      <label htmlFor="contactName">Name</label>
                      <input type="text" className="form-control" id="contactName" placeholder="Enter contact nickname" />
                    </div>
                  </form>
                </div>
                <div className="modal-footer">

                <button type="button" className="btn btn-secondary" data-dismiss="modal">
                    Close
                  </button>
                  <button
                    type="button"
                    className="btn btn-primary"
                    onClick={() => addContact(document.getElementById("contactName").value)}
                    data-dismiss="modal"
                  >
                    Add
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
        <div id="chat">
          <div id="chat_header">
            <img src={currentPicture} alt="profile picture" id="chat_profile_picture_2" />
            <b className="textColor contatcs_chat">{currentChat}</b>
          </div>

          <div id="chat_box">
            {chats.map((chat, index) => (
              <BubbleChat key={index} text={chat.text} time={chat.time} sender={chat.sender} />
            ))}
          </div>

          <div id="send_message">
            <input type="text" className="form-control rounded-0" id="send" aria-describedby="emailHelp" placeholder="Enter your message here..." />
            <button type="button" className="btn btn-outline-light btn-sm textColor" id="send_btn">
              Send
            </button>
            <button type="button" className="btn btn-outline-light btn-sm textColor" id="send_btn">
              Attach A File
            </button>
          </div>
        </div>
      </main>

      <script src="https://code.jquery.com/jquery-3.3.1.slim.min.js" integrity="sha384-q8i/X+965DzO0rT7abK41JStQIAqVgRVzpbzo5smXKp4YfRvH+8abtTE1Pi6jizo" crossorigin="anonymous"></script>
      <script src="https://cdnjs.cloudflare.com/ajax/libs/popper.js/1.14.7/umd/popper.min.js" integrity="sha384-UO2eT0CpHqdSJQ6hJty5KVphtPhzWj9WO1clHTMGa3JDZwrnQq4sF86dIHNDz0W1" crossorigin="anonymous"></script>
      <script src="https://stackpath.bootstrapcdn.com/bootstrap/4.3.1/js/bootstrap.min.js" integrity="sha384-JjSmVgyd0p3pXB1rRibZUAYoIIy6OrQ6VrjIEaFf/nJGzIxFDsf4x0xIM+B07jRM" crossorigin="anonymous"></script>
    </>
  );
}

export default ChatPage;
